import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';

const Wrapper = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  height: 10%;
  background-color: white;
`;

const Stats = props => (
  <Wrapper
    className={"stats"}
  >
    <div><div>Total Vehicles</div> {props.data.length}</div>
    <div><div>Trains</div> {props.data.filter(i => i.type === 'rail').length}</div>
    <div><div>Busses</div> {(function(){var busses = props.data.filter(i => i.type === 'bus'); return busses.length})()}</div>
    <div><div>Delayed</div> {(function(){var delayed = props.data.filter(i => i.delay > 0); return delayed.length})()}</div>
    <div><div>Ahead of Schedule</div> {(function(){var delayed = props.data.filter(i => i.delay < 0); return delayed.length})()}</div>
    <div><div>On Schedule</div> {(function(){var onSched = props.data.filter(i => i.delay == 0); return onSched.length})()}</div>
    <div><div>In Congestion</div> {(function(){var inCongestion = props.data.filter(i => i.inCongestion === true); return inCongestion.length})()}</div>
  </Wrapper>
);

Stats.defaultProps = {
  onClick: null,
};

Stats.propTypes = {
  onClick: PropTypes.func,
  text: PropTypes.string.isRequired,
};

export default Stats;
