import React from "react";
import GoogleMap from './components/GoogleMap';

import Marker from './components/Marker';
import Navigation from './components/Navigation';
import Stats from './components/Stats';
import { apiIsLoaded } from './Functions';

import "./App.css";

const API_URL = "https://developer.trimet.org/ws/v2/vehicles/appID/CC57D2038B76DBBD253D6A587";
const defaultCenter = [45.519526,-122.677040];


export class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoaded: false,
      items: []
    };
  }

  async componentDidMount() {
    try {
      setInterval(async () => {
        const res = await fetch(API_URL);
        const json = await res.json();
        const filteredVehicles = json.resultSet.vehicle.filter(vehicle => vehicle.type == 'rail');
        const newData = filteredVehicles;

        this.setState({
          isLoaded: true,
          items: newData
        })
      }, 4000);
    } catch(e) {
      console.log(e);
    }
  }

  render() {
    var { isLoaded, items } = this.state;

    if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div className="container">
          <GoogleMap
            defaultZoom={12}
            defaultCenter={defaultCenter}
            yesIWantToUseGoogleMapApiInternals
            onGoogleApiLoaded={({ map, maps }) => apiIsLoaded(map, maps, items)}
            >
            {items.map(item => (
              <Marker 
              key={item.vehicleID}
              text={item.signMessageLong}
              type={item.type}
              lat={item.latitude}
              lng={item.longitude}
              data={item}
              />
            ))}
          </GoogleMap>
          <Navigation />
          <Stats data={items} />
        </div>
      );
    }
  }
}

export default App;
