import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';

const Wrapper = styled.div`
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  height: 25%;
  background-color: white;
`;


const Stats = props => (


  <Wrapper
    className={"stats"}
  >
  <div>
      <div>Total Vehicles: {props.data.length}</div>
      <div>Trains: {(function(){var trains = props.data.filter(i => i.type == 'rail'); return trains.length})()}</div>
      <div>Busses: {(function(){var busses = props.data.filter(i => i.tyle == 'bus'); return busses.length})()}</div>
      <div>Delayed: {(function(){var delayed = props.data.filter(i => i.delay > 0); return delayed.length})()}</div>
      <div>Ahead of Schedule: {(function(){var delayed = props.data.filter(i => i.delay < 0); return delayed.length})()}</div>
      <div>Right on Schedule: {(function(){var onSched = props.data.filter(i => i.delay == 0); return onSched.length})()}</div>
      <div>In Congestion: {(function(){var inCongestion = props.data.filter(i => i.inCongestion === true); return inCongestion.length})()}</div>
  </div>
  </Wrapper>
);

Stats.defaultProps = {
  onClick: null,
};

Stats.propTypes = {
  onClick: PropTypes.func,
  text: PropTypes.string.isRequired,
};

export default Stats;
