import React from "react";
import GoogleMap from './components/GoogleMap';

import Marker from './components/Marker';
import Stats from './components/Stats';
import { apiIsLoaded } from './components/Functions';
import "./App.css";
import { mapStyles } from './mapstyles.js';
// import { mapStyles } from './mapstyles-bw.js';

const API_URL = "https://api.wmata.com/Bus.svc/json/jBusPositions";
const defaultCenter = [38.975886,-77.023464];


export class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoaded: false,
      vehicles: []
    };
  }

  async componentDidMount() {
    let totalRequests = 0;
    try {
      setInterval(async () => {
        if (totalRequests < 10) {
          const res = await fetch(API_URL, {
            headers: new Headers({
              "api_key": "e13626d03d8e4c03ac07f95541b3091b"
            })
          }
            );
          const json = await res.json();
          // const filteredVehicles = json.resultSet.vehicle.filter(vehicle => vehicle.type === 'rail');
          const vehicles = json.BusPositions;

          this.setState({
            isLoaded: true,
            vehicles: vehicles
          })
          totalRequests++;
        }
      }, 10000);
    } catch(e) {
      console.error(e);
    }
  }

  render() {
    let { isLoaded, vehicles } = this.state;

    if (!isLoaded) {
      return <div className="loading">Loading<span>.</span><span>.</span><span>.</span></div>;
    } else {
      return (
        <div className="container">
          <GoogleMap
            defaultZoom={12}
            defaultCenter={defaultCenter}
            options={{
              styles: mapStyles
            }}
            yesIWantToUseGoogleMapApiInternals
            onGoogleApiLoaded={({ map, maps }) => apiIsLoaded(map, maps, vehicles)}
            >
            {vehicles.map(vehicle => (
              <Marker 
              key={vehicle.vehicleID}
              lat={vehicle.Lat}
              lng={vehicle.Lon}
              />
            ))}
          </GoogleMap>
        </div>
      );
    }
  }
}

export default App;
